sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	gs.info("Customer Name   " + current.customer_name.getDisplayValue());
	var total_credit=0;
	var total_debit=0;
	var onion = new GlideRecord('x_374502_onion_man_customer_transaction');
	onion.addQuery('customer_name',current.customer_name);	
	onion.query();

	while(onion.next()){
		//gs.info("Credit   " +onion.credit + "Debit    " +onion.debit);
		if(onion.credit != null && onion.credit != '0'){
		total_credit= parseInt(total_credit)+parseInt(onion.credit);
			gs.info("total_credit  "+total_credit.toString());
		}
		if(onion.debit != null && onion.debit != '0'){
		total_debit=parseInt(total_debit)+parseInt(onion.debit);
			gs.info("total_debit  "+total_debit.toString());
		}
	}

	gs.info("Total Credit   "+total_credit + "Total Debit    "+total_debit);
	var total_balance = parseInt(total_credit)-parseInt(total_debit);
	gs.info("Current balance is   "+current.balance);
	var CDType = current.transaction_details;

	gs.info("Tran Type is:   " + CDType  + " Tran Val is:    " + current.amount);
	if(CDType == 'Credit Transaction')
		{
			current.credit = current.amount;
			current.debit = '0';
			 current.balance= parseInt(total_balance)+parseInt(current.credit);
			gs.info("current balance   ="  + current.balance);
	}
	else if(CDType == 'Debit Transaction')
		{
			current.debit = current.amount;
			current.credit = '0';
			current.balance = parseInt(total_balance)-parseInt(current.debit);
			gs.info("current balance   ="   +current.balance);
	}

})(current, previous);
